import { Component, OnInit, Output, EventEmitter } from '@angular/core';
import { Recipe } from '../recipe.model';

@Component({
  selector: 'app-recipe-list',
  templateUrl: './recipe-list.component.html',
  styleUrls: ['./recipe-list.component.css']
})
export class RecipeListComponent implements OnInit {

  @Output() selected : Recipe;

  recipes: Recipe[] = [
    new Recipe("Test", "This is a test", "data:image/jpeg;base64," ), new Recipe("Test", "This is a test", "data:image/jpeg;base64,")
  ]

  @Output() sendSelected = new EventEmitter<Recipe>()

  constructor() { }

  ngOnInit(): void {
  }

  handleRecipeSelect = (recipe : Recipe) => {
    this.sendSelected.emit(recipe)
  }
}
